shaders.vert:
#define _IS_VERT_SHADER 1

precision highp float;

uniform mat4 cc_matWorld;
uniform mat3 cc_matWorldIT;

uniform mat4 cc_matView;

uniform mat4 cc_matViewProj;

uniform vec3 cc_cameraPos;                       

uniform vec3 cc_sceneAmbient;                    

#ifndef USE_DIFFUSE_TEXTURE
  #ifndef USE_EMISSIVE_TEXTURE
    #ifndef USE_SPECULAR_TEXTURE
      #ifndef USE_NORMAL_TEXTURE
        #define _NOT_USE_TEXTURE 1
      #endif
    #endif
  #endif
#endif

#if USE_TILING_OFFSET && _USE_ATTRIBUTE_UV0
  uniform vec2 mainTiling;
  uniform vec2 mainOffset;
#endif

#ifdef _IS_VERT_SHADER
  attribute vec3 a_position;
#endif

#ifndef _NOT_USE_TEXTURE

  #if _USE_ATTRIBUTE_UV0
    #ifdef _IS_VERT_SHADER
      attribute mediump vec2 a_uv0;
    #endif

    varying mediump vec2 v_uv0;
  #endif

#endif

#if _USE_ATTRIBUTE_COLOR

  #ifdef _IS_VERT_SHADER
    attribute lowp vec4 a_color;
  #endif

  varying lowp vec4 v_color;
#endif

#if _USE_ATTRIBUTE_NORMAL
  #ifdef _IS_VERT_SHADER
    attribute vec3 a_normal;
  #endif
#endif

#ifdef _IS_VERT_SHADER

  void ATTRIBUTE_TO_VARYING () {

    #if _USE_ATTRIBUTE_COLOR
        v_color = a_color;
    #endif

    #ifndef _NOT_USE_TEXTURE
      #if _USE_ATTRIBUTE_UV0
        v_uv0 = a_uv0;

        #if USE_TILING_OFFSET
          v_uv0 = v_uv0 * mainTiling + mainOffset;
        #endif
      #endif
    #endif

  }

#endif

void MUL_ATTR_COLOR (inout vec4 color) {
  #if _USE_ATTRIBUTE_COLOR
    #ifdef _IS_VERT_SHADER
      color *= a_color;
    #else
      color *= v_color;
    #endif
  #endif
}

void MUL_ATTR_NORMAL (inout vec3 normal) {
  #if _USE_ATTRIBUTE_NORMAL
    #ifdef _IS_VERT_SHADER
      normal *= a_normal;
    #endif
  #endif
}
void MUL_ATTR_NORMAL (inout vec4 normal) {
  #if _USE_ATTRIBUTE_NORMAL
    #ifdef _IS_VERT_SHADER
      normal.xyz *= a_normal;
    #endif
  #endif
}

#if _USE_SKINNING

  attribute vec4 a_weights;
  attribute vec4 a_joints;

  #if _USE_JOINTS_TEXTRUE
  uniform sampler2D _jointsTexture;
  uniform float _jointsTextureSize;

  mat4 getBoneMatrix(const in float i) {
    float size = _jointsTextureSize;
    float j = i * 4.0;
    float x = mod(j, size);
    float y = floor(j / size);

    float dx = 1.0 / size;
    float dy = 1.0 / size;

    y = dy * (y + 0.5);

    vec4 v1 = texture2D(_jointsTexture, vec2(dx * (x + 0.5), y));
    vec4 v2 = texture2D(_jointsTexture, vec2(dx * (x + 1.5), y));
    vec4 v3 = texture2D(_jointsTexture, vec2(dx * (x + 2.5), y));
    vec4 v4 = texture2D(_jointsTexture, vec2(dx * (x + 3.5), y));

    return mat4(v1, v2, v3, v4);
  }
  #else
  uniform mat4 _jointMatrices[_JOINT_MATRICES_SIZE];

  mat4 getBoneMatrix(const in float i) {
    return _jointMatrices[int(i)];
  }
  #endif

  mat4 skinMatrix() {
    return
      getBoneMatrix(a_joints.x) * a_weights.x +
      getBoneMatrix(a_joints.y) * a_weights.y +
      getBoneMatrix(a_joints.z) * a_weights.z +
      getBoneMatrix(a_joints.w) * a_weights.w
      ;
  }
#endif

void SKIN_VERTEX(inout vec4 a1) {
  #if _USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
  #endif
}

void SKIN_VERTEX(inout vec4 a1, inout vec4 a2) {
  #if _USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
    a2 = m * a2;
  #endif
}

void SKIN_VERTEX(inout vec4 a1, inout vec4 a2, inout vec4 a3) {
  #if _USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
    a2 = m * a2;
    a3 = m * a3;
  #endif
}

void main () {
  vec4 position = vec4(a_position, 1);

  SKIN_VERTEX(position);
  ATTRIBUTE_TO_VARYING();

  gl_Position = cc_matViewProj * cc_matWorld * position;
}

