vec3 gammaToLinearSpaceRGB(in vec3 sRGB) { 
  return sRGB * (sRGB * (sRGB * 0.305306011 + 0.682171111) + 0.012522878);
}

vec3 linearToGammaSpaceRGB(in vec3 RGB) { 
  vec3 S1 = sqrt(RGB);
  vec3 S2 = sqrt(S1);
  vec3 S3 = sqrt(S2);
  return 0.585122381 * S1 + 0.783140355 * S2 - 0.368262736 * S3;
}

vec4 gammaToLinearSpaceRGBA(in vec4 sRGBA) {
  return vec4(gammaToLinearSpaceRGB(sRGBA.rgb), sRGBA.a);
}

vec4 linearToGammaSpaceRGBA(in vec4 RGBA) {
  return vec4(linearToGammaSpaceRGB(RGBA.rgb), RGBA.a);
}

vec4 linearToLinear (in vec4 value) {
  return value;
}

#if INPUT_IS_GAMMA
  #define TEXEL_TO_LINEAR gammaToLinearSpaceRGBA
#else
  #define TEXEL_TO_LINEAR linearToLinear
#endif

#if OUTPUT_TO_GAMMA
  #define LINEAR_TO_OUTPUT_TEXEL linearToGammaSpaceRGBA
#else
  #define LINEAR_TO_OUTPUT_TEXEL linearToLinear
#endif

uniform lowp vec4 diffuseColor;

#if USE_DIFFUSE_TEXTURE
  uniform sampler2D diffuseTexture;
#endif

void MULTIPLY_DIFFUSE_TEXTRUE_COLOR (inout vec4 color, in vec2 uv) {
  #if USE_DIFFUSE_TEXTURE && _USE_ATTRIBUTE_UV0
    vec4 diffuseTextureColor = texture2D(diffuseTexture, uv);
    #if _USE_ETC1_DIFFUSETEXTURE
      diffuseTextureColor.a *= texture2D(diffuseTexture, uv + vec2(0, 0.5)).r;
    #endif
    color *= TEXEL_TO_LINEAR(diffuseTextureColor);
  #endif
}

void MULTIPLY_DIFFUSE_TEXTRUE_COLOR (inout vec4 color) {
  #if USE_DIFFUSE_TEXTURE && _USE_ATTRIBUTE_UV0

    #ifdef _IS_VERT_SHADER
      vec2 uv = a_uv0;
    #else
      vec2 uv = v_uv0;
    #endif

    MULTIPLY_DIFFUSE_TEXTRUE_COLOR(color, uv);
  #endif
}

void CALC_DIFFUSE (inout vec4 diffuse, in vec2 uv) {
  diffuse = diffuseColor;

  MUL_ATTR_COLOR(diffuse);
  MULTIPLY_DIFFUSE_TEXTRUE_COLOR(diffuse, uv);
}

void CALC_DIFFUSE (inout vec4 diffuse) {
  diffuse = diffuseColor;

  MUL_ATTR_COLOR(diffuse);
  MULTIPLY_DIFFUSE_TEXTRUE_COLOR(diffuse);
}

#if USE_ALPHA_TEST
  uniform float alphaThreshold;
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

void main () {
  vec4 diffuse;
  CALC_DIFFUSE(diffuse);
  ALPHA_TEST(diffuse);
  gl_FragColor = LINEAR_TO_OUTPUT_TEXEL( diffuse );
}
