}

#if _USE_SKINNING

  attribute vec4 a_weights;
  attribute vec4 a_joints;

  #if _USE_JOINTS_TEXTRUE
  uniform sampler2D _jointsTexture;
  uniform float _jointsTextureSize;

  mat4 getBoneMatrix(const in float i) {
    float size = _jointsTextureSize;
    float j = i * 4.0;
    float x = mod(j, size);
    float y = floor(j / size);

    float dx = 1.0 / size;
    float dy = 1.0 / size;

    y = dy * (y + 0.5);

    vec4 v1 = texture2D(_jointsTexture, vec2(dx * (x + 0.5), y));
    vec4 v2 = texture2D(_jointsTexture, vec2(dx * (x + 1.5), y));
    vec4 v3 = texture2D(_jointsTexture, vec2(dx * (x + 2.5), y));
    vec4 v4 = texture2D(_jointsTexture, vec2(dx * (x + 3.5), y));

    return mat4(v1, v2, v3, v4);
  }
  #else
  uniform mat4 _jointMatrices[_JOINT_MATRICES_SIZE];

  mat4 getBoneMatrix(const in float i) {
    return _jointMatrices[int(i)];
  }
  #endif

  mat4 skinMatrix() {
    return
      getBoneMatrix(a_joints.x) * a_weights.x +
      getBoneMatrix(a_joints.y) * a_weights.y +
      getBoneMatrix(a_joints.z) * a_weights.z +
      getBoneMatrix(a_joints.w) * a_weights.w
      ;
  }
#endif

void SKIN_VERTEX(inout vec4 a1) {
  #if _USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
  #endif
}

void SKIN_VERTEX(inout vec4 a1, inout vec4 a2) {
  #if _USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
    a2 = m * a2;
  #endif
}

void SKIN_VERTEX(inout vec4 a1, inout vec4 a2, inout vec4 a3) {
  #if _USE_SKINNING
    mat4 m = skinMatrix();
    a1 = m * a1;
    a2 = m * a2;
    a3 = m * a3;
  #endif
}


#if _USE_SHADOW_MAP
  #if _NUM_SHADOW_LIGHTS > 0
    #pragma for id in range(0, _NUM_SHADOW_LIGHTS)
      uniform mat4 cc_lightViewProjMatrix_{id};
      uniform float cc_minDepth_{id};
      uniform float cc_maxDepth_{id};
      varying vec4 v_posLightSpace{id};
      varying float v_depth{id};
    #pragma endFor
  #endif
#endif

void CLAC_SHADOW () {
  #if _USE_SHADOW_MAP
    #if _NUM_SHADOW_LIGHTS > 0
      #pragma for id in range(0, _NUM_SHADOW_LIGHTS)
        v_posLightSpace{id} = cc_lightViewProjMatrix_{id} * vec4(v_worldPos, 1.0);
        v_depth{id} = (v_posLightSpace{id}.z + cc_minDepth_{id}) / (cc_minDepth_{id} + cc_maxDepth_{id});
      #pragma endFor
    #endif
  #endif
}

void main () {
  vec4 position = vec4(a_position, 1);
  vec4 normal = vec4(1, 1, 1, 0);

  MUL_ATTR_NORMAL(normal);
  SKIN_VERTEX(position, normal);

  v_worldPos = (cc_matWorld * position).xyz;
  v_worldNormal = cc_matWorldIT * normal.xyz;

  CLAC_SHADOW();
  ATTRIBUTE_TO_VARYING();

  gl_Position = cc_matViewProj * cc_matWorld * position;
}

