#endif

#if _NUM_POINT_LIGHTS > 0
  uniform vec4 cc_pointLightPositionAndRange[_NUM_POINT_LIGHTS];   
  uniform vec4 cc_pointLightColor[_NUM_POINT_LIGHTS];              
#endif

#if _NUM_SPOT_LIGHTS > 0
  uniform vec4 cc_spotLightPositionAndRange[_NUM_SPOT_LIGHTS];    
  uniform vec4 cc_spotLightDirection[_NUM_SPOT_LIGHTS];           
  uniform vec4 cc_spotLightColor[_NUM_SPOT_LIGHTS];               
#endif

struct LightInfo {
  vec3 lightDir;
  vec3 radiance;
};

LightInfo computeDirectionalLighting(
  vec4 lightDirection,
  vec4 lightColor
) {
  LightInfo ret;
  ret.lightDir = -normalize(lightDirection.xyz);
  ret.radiance = lightColor.rgb;
  return ret;
}

LightInfo computePointLighting(
  vec3 worldPosition,
  vec4 lightPositionAndRange,
  vec4 lightColor
) {
  LightInfo ret;
  vec3 lightDir = lightPositionAndRange.xyz - worldPosition;
  float attenuation = max(0., 1.0 - length(lightDir) / lightPositionAndRange.w);

  ret.lightDir = normalize(lightDir);
  ret.radiance = lightColor.rgb * attenuation;
  return ret;
}

LightInfo computeSpotLighting(
  vec3 worldPosition,
  vec4 lightPositionAndRange,
  vec4 lightDirection,
  vec4 lightColor
) {
  LightInfo ret;
  vec3 lightDir = lightPositionAndRange.xyz - worldPosition;
  float attenuation = max(0., 1.0 - length(lightDir) / lightPositionAndRange.w);
  lightDir = normalize(lightDir);
  float cosConeAngle = max(0., dot(lightDirection.xyz, -lightDir));
  cosConeAngle = cosConeAngle < lightDirection.w ? 0. : cosConeAngle;
  cosConeAngle = pow(cosConeAngle, lightColor.w);

  ret.lightDir = lightDir;
  ret.radiance = lightColor.rgb * attenuation * cosConeAngle;
  return ret;
}

uniform float glossiness;

struct PhongMaterial {
  vec3 diffuse;
  vec3 emissive;
  vec3 specular;
  float opacity;
};

struct Lighting {
  vec3 diffuse;
  vec3 specular;
};

void brdf (inout Lighting result, LightInfo info, vec3 normal, vec3 viewDirection, float glossiness) {
  float ndh = 0.0;

  vec3 halfDir = normalize(viewDirection + info.lightDir);
  float NdotH = max(0.0, dot(normal, halfDir));
  NdotH = pow(NdotH, max(1.0, glossiness * 128.0));

  vec3 diffuse = info.radiance * max(0.0, dot(normal, info.lightDir));
  vec3 specular = info.radiance * NdotH;

  result.diffuse += diffuse;
  result.specular += specular;
}

Lighting getPhongLighting (vec3 normal, vec3 worldPos, vec3 viewDirection, float glossiness) {
  Lighting result;
  result.diffuse = vec3(0, 0, 0);
  result.specular = vec3(0, 0, 0);

  #if _NUM_DIR_LIGHTS > 0
  for (int i = 0; i < _NUM_DIR_LIGHTS; i++) {
    LightInfo info = computeDirectionalLighting(cc_dirLightDirection[i], cc_dirLightColor[i]);
    brdf(result, info, normal, viewDirection, glossiness);
  }
  #endif

  #if _NUM_POINT_LIGHTS > 0
  for (int i = 0; i < _NUM_POINT_LIGHTS; i++) {
    LightInfo info = computePointLighting(worldPos, cc_pointLightPositionAndRange[i], cc_pointLightColor[i]);
    brdf(result, info, normal, viewDirection, glossiness);
  }
  #endif

  #if _NUM_SPOT_LIGHTS > 0
  for (int i = 0; i < _NUM_SPOT_LIGHTS; i++) {
    LightInfo info = computeSpotLighting(worldPos, cc_spotLightPositionAndRange[i], cc_spotLightDirection[i], cc_spotLightColor[i]);
    brdf(result, info, normal, viewDirection, glossiness);
  }
  #endif

  result.diffuse += cc_sceneAmbient;

  return result;
}

vec4 composePhongShading (Lighting lighting, PhongMaterial mtl) {
  vec4 o = vec4(0.0, 0.0, 0.0, 1.0);


  o.rgb = lighting.diffuse * mtl.diffuse;

  #if USE_EMISSIVE
    o.rgb += mtl.emissive;
  #endif

  #if USE_SPECULAR
    o.rgb += lighting.specular * mtl.specular;
  #endif

  #if _USE_SHADOW_MAP
    o.rgb *= computeShadow();
  #endif

  o.a = mtl.opacity;

  return o;
}

void CALC_PHONG_LIGHTING (inout vec4 outColor, in PhongMaterial material, in vec3 normal, in vec3 worldPosition, in vec3 viewDirection) {
  Lighting phongLighting = getPhongLighting(normal, worldPosition, viewDirection, glossiness);
  outColor = composePhongShading(phongLighting, material);
}

#if USE_ALPHA_TEST
  uniform float alphaThreshold;
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

void main () {
  vec4 diffuse;
  CALC_DIFFUSE(diffuse);
  ALPHA_TEST(diffuse);

  PhongMaterial material;
  material.diffuse = diffuse.rgb;
  material.opacity = diffuse.a;

  CALC_EMISSIVE(material.emissive);
  CALC_SPECULAR(material.specular);

  vec3 viewDirection = normalize(cc_cameraPos.xyz - v_worldPos);
  vec3 normal;
  vec4 phongColor;

  CALC_NORMAL(normal, v_worldPos, v_worldNormal);
  CALC_PHONG_LIGHTING(phongColor, material, normal, v_worldPos, viewDirection);

  gl_FragColor = LINEAR_TO_OUTPUT_TEXEL( phongColor );
}

shaders.vert:
#define _IS_VERT_SHADER 1

precision highp float;

attribute vec3 a_position;

uniform mat4 cc_matWorld;
uniform mat4 cc_lightViewProjMatrix;
uniform float cc_minDepth;
uniform float cc_maxDepth;
uniform float cc_bias;
varying float vDepth;

#if _USE_SKINNING

  attribute vec4 a_weights;
  attribute vec4 a_joints;

  #if _USE_JOINTS_TEXTRUE
  uniform sampler2D _jointsTexture;
  uniform float _jointsTextureSize;

  mat4 getBoneMatrix(const in float i) {
    float size = _jointsTextureSize;
    float j = i * 4.0;
    float x = mod(j, size);
    float y = floor(j / size);

    float dx = 1.0 / size;
    float dy = 1.0 / size;

    y = dy * (y + 0.5);

    vec4 v1 = texture2D(_jointsTexture, vec2(dx * (x + 0.5), y));
    vec4 v2 = texture2D(_jointsTexture, vec2(dx * (x + 1.5), y));
    vec4 v3 = texture2D(_jointsTexture, vec2(dx * (x + 2.5), y));
    vec4 v4 = texture2D(_jointsTexture, vec2(dx * (x + 3.5), y));

    return mat4(v1, v2, v3, v4);
  }
  #else
  uniform mat4 _jointMatrices[_JOINT_MATRICES_SIZE];

  mat4 getBoneMatrix(const in float i) {
    return _jointMatrices[int(i)];
  }
  #endif

  mat4 skinMatrix() {
    return
      getBoneMatrix(a_joints.x) * a_weights.x +
      getBoneMatrix(a_joints.y) * a_weights.y +
